// Call the dataTables jQuery plugin
$(document).ready(function() {
  // Força a sidebar a ficar recolhida em desktop
  if ($(window).width() > 768) {
    $("body").addClass("sidebar-toggled");
    $(".sidebar").addClass("toggled");
  }

  // Adiciona tooltips aos itens do menu
  $('.sidebar .nav-item .nav-link').each(function() {
    const text = $(this).find('span').text().trim();
    $(this).attr('data-title', text);
  });

  // Cria o menu mobile e overlay
  if ($('.mobile-menu').length === 0) {
    const mobileMenu = `
      <div class="mobile-menu">
        <div class="mobile-menu-header">
          <h5>Menu</h5>
          <button class="mobile-menu-close">
            <i class="fas fa-times"></i>
          </button>
        </div>
        <div class="mobile-menu-body">
          ${$('#accordionSidebar').html()}
        </div>
      </div>
      <div class="mobile-menu-overlay"></div>
    `;
    $('body').append(mobileMenu);
  }

  // Toggle do menu mobile
  $("#sidebarToggleTop").on('click', function(e) {
    e.preventDefault();
    $('.mobile-menu, .mobile-menu-overlay').addClass('show');
    $('body').addClass('mobile-menu-open');
  });

  // Fecha o menu mobile
  $(".mobile-menu-close, .mobile-menu-overlay").on('click', function() {
    $('.mobile-menu, .mobile-menu-overlay').removeClass('show');
    $('body').removeClass('mobile-menu-open');
  });

  // Manipula os submenus no menu mobile
  $('.mobile-menu .nav-link[data-toggle="collapse"]').on('click', function(e) {
    e.preventDefault();
    const $this = $(this);
    const $target = $($this.attr('data-target'));
    
    // Fecha outros submenus abertos
    $('.mobile-menu .collapse').not($target).collapse('hide');
    
    // Toggle do submenu atual
    $target.collapse('toggle');
    
    // Rotaciona o ícone
    $this.find('.submenu-icon').toggleClass('rotate-90');
  });

  // Fecha o menu ao clicar em um link (em mobile)
  $('.mobile-menu .nav-link:not([data-toggle="collapse"])').on('click', function() {
    if ($(window).width() <= 768) {
      $('.mobile-menu, .mobile-menu-overlay').removeClass('show');
      $('body').removeClass('mobile-menu-open');
    }
  });

  // Ajusta o layout quando redimensionar a janela
  let resizeTimer;
  $(window).on('resize', function() {
    clearTimeout(resizeTimer);
    resizeTimer = setTimeout(function() {
      if ($(window).width() > 768) {
        $('.mobile-menu, .mobile-menu-overlay').removeClass('show');
        $('body').removeClass('mobile-menu-open');
      }
    }, 250);
  });

  // Previne scroll do content quando hover na sidebar
  $('.sidebar').on('mousewheel DOMMouseScroll wheel', function(e) {
    if ($(this).get(0).scrollHeight > $(this).height()) {
      var e0 = e.originalEvent;
      var delta = e0.wheelDelta || -e0.detail;
      this.scrollTop += (delta < 0 ? 1 : -1) * 30;
      e.preventDefault();
    }
  });

  // Scroll to top button
  $(document).on('scroll', function() {
    var scrollDistance = $(this).scrollTop();
    if (scrollDistance > 100) {
      $('.scroll-to-top').fadeIn();
    } else {
      $('.scroll-to-top').fadeOut();
    }
  });

  // Smooth scrolling
  $(document).on('click', 'a.scroll-to-top', function(e) {
    e.preventDefault();
    $('html, body').animate({
      scrollTop: 0
    }, 500);
  });
});